import React from 'react'

const Course = (props) => 
{
  console.log(props.course)
  return (
      <Header name={props.course}/>
  )
}

const Part = (props) => 
{
  return (
    <div>
      {props.courseparts.map(element => (
        <p>{element.name} No. of exercices = {element.exercises}</p> 
      ))}
     </div> 
  )
}   


const Header = (props) => 
{
  return (
    <div>
      <p>{props.name}</p>
    </div>
  )
}

const Content = (props) =>
{
  return (
    <div>
      <Part courseparts={props.course}/>
    </div>
  )
}

const Total = (props) =>
{
  return (
    <div>Total = {props.course.reduce((prev, next) => prev + next.exercises, 0)}</div>
  )
}

const App = () => {
  const course = { 
    id: 1,
    courseName : 'HALF Stack application development',
    parts : [
    {
      id:1,
      name :'Fundamentals of REACT',
      exercises : 10
    },
    {
      id:2,
      name : 'Using props to pass data',
      exercises : 7
    },
    {
      id:3,
      name :'State of a component',
      exercises : 14
    }
  ]
  }
  return <Course key={course.id} course={course.courseName}/>
}

export default App
